import React, { useState } from "react";
import { Typography, TextField, Grid, Button } from "@mui/material";
import "./App.css";
import logo from "./images/light-logo.png";
import Switch from "@mui/material/Switch";
const label = { inputProps: { "aria-label": "Size switch demo" } };

function App() {

  const [isSwitchOn, setIsSwitchOn] = useState(true);
  const popupStyle = {
    backgroundColor: isSwitchOn ? "rgb(100, 64, 189)" : "gray",

    transition: "background-color 0.5s",
  };

  const textFieldStyle = {
    backgroundColor: "white",
    marginTop: "10px",
    width: "400px",
    borderRadius: "20px",
  };

  return (
    <div className="App" style={popupStyle}>
      <Grid container>
        <img
          style={{ width: "200px", marginLeft: "50px", padding: "10px" }}
          src={logo}
          alt="logo"
        />
        <Typography style={{ marginLeft: "60px", padding: "10px" }}>
          Sign in to your account
        </Typography>

        <TextField
          id="outlined-basic"
          label="Enter your email..."
          variant="outlined"
          style={textFieldStyle}
        />
        <div style={{ marginLeft: "260px", marginTop: "10px" }}>Forgot?</div>
        <TextField
          id="outlined-basic"
          label="Enter your password..."
          variant="outlined"
          style={textFieldStyle}
        />

        <Grid style={{ display: "flex", marginTop: "25px" }}>
          <Typography style={{ marginTop: "10px", fontSize:"135x" }}>No Account?</Typography>
          <Typography style={{ marginLeft: "15px", marginTop: "10px" }}>
            Sign Up
          </Typography>
          <Button
            style={{
              backgroundColor: "white",
              width: "130px",
              height: "50px",
              marginLeft: "40px",
              borderRadius: "20px",
            }}
          >
            Sign In
          </Button>
        </Grid>
        <Grid
          container
          style={{
            display: "flex",
            justifyContent: "space-between",
            marginTop: "30px",
          }}
        >
          <Typography>Dark Theme</Typography>
          <Switch
            {...label}
            color="default"
            checked={isSwitchOn}
            onChange={() => setIsSwitchOn(!isSwitchOn)}
          />
        </Grid>
      </Grid>
    </div>
  );
}

export default App;
