import React, { useState } from "react";
import { Typography, TextField, Grid, Button, Link } from "@mui/material";
import Switch from "@mui/material/Switch";

import ForgotPass from "./component/ForgotPass";
import "./App.css";
import logo from "./images/light-logo.png";

function App() {
  const [isSwitchOn, setIsSwitchOn] = useState(true);
  const [showForgotScreen, setShowForgotScreen] = useState(false);
  const label = { inputProps: { "aria-label": "Size switch demo" } };
  const popupStyle = {
    backgroundColor: isSwitchOn ? "rgb(100, 64, 189)" : "gray",
    transition: "background-color 0.5s",
  };

  const textFieldStyle = {
    backgroundColor: "white",
    marginTop: "10px",
    width: "400px",
    borderRadius: "20px",
  };

  const handleForgotLinkClick = () => {
    setShowForgotScreen(true);
  };

  return (
    <div className="App" style={popupStyle}>
      {showForgotScreen ? (
        <div>
          <img
            style={{ width: "200px", padding: "10px" }}
            src={logo}
            alt="logo"
          />
          <Typography style={{  padding: "10px" }}>
            Reset password
          </Typography>
          <TextField
            id="outlined-basic"
            label="Enter your email..."
            variant="outlined"
            style={{
              backgroundColor: "white",
              width: "300px",
              borderRadius: "20px",
              marginTop:"10px"
            }}
          />
          <Button
              style={{
                backgroundColor: "white",
                width: "300px",
                height: "50px",
                marginLeft: "40px",
                borderRadius: "20px",
                fontWeight: "bold",
              }}
            >
              Send password reset link
            </Button>
        </div>
      ) : (
        <Grid container>
          <img
            style={{ width: "200px", marginLeft: "50px", padding: "10px" }}
            src={logo}
            alt="logo"
          />
          <Typography style={{ marginLeft: "60px", padding: "10px" }}>
            Sign in to your account
          </Typography>

          <TextField
            id="outlined-basic"
            label="Enter your email..."
            variant="outlined"
            style={textFieldStyle}
          />
          <div
            style={{ marginLeft: "260px", marginTop: "10px", fontSize: "14px" }}
          >
            <Link
              style={{ color: "white", cursor: "pointer" }}
              onClick={handleForgotLinkClick}
            >
              Forgot?
            </Link>
          </div>
          <TextField
            id="outlined-basic"
            label="Enter your password..."
            variant="outlined"
            style={textFieldStyle}
          />

          <Grid style={{ display: "flex", marginTop: "25px" }}>
            <Typography style={{ marginTop: "10px", fontSize: "14px" }}>
              No Account?
            </Typography>
            <Typography
              style={{
                marginLeft: "15px",
                marginTop: "10px",
                fontSize: "14px",
              }}
            >
              Sign Up
            </Typography>
            <Button
              style={{
                backgroundColor: "white",
                width: "130px",
                height: "50px",
                marginLeft: "40px",
                borderRadius: "20px",
                fontWeight: "bold",
              }}
            >
              Sign In
            </Button>
          </Grid>
          <Grid
            container
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "30px",
            }}
          >
            <Typography>Dark Theme</Typography>
            <Switch
              {...label}
              color="default"
              checked={isSwitchOn}
              onChange={() => setIsSwitchOn(!isSwitchOn)}
            />
          </Grid>
        </Grid>
      )}
    </div>
  );
}

export default App;
